mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select s.*, c.name as categoryName
from setmeal s left join category c on s.category_id = c.id

    and s.category_id = #{categoryId}
    and s.status = #{status}
    and s.name like concat('%',#{name},'%')

order by s.update_time desc

select s.*, c.name as categoryName
from setmeal s left join category c on s.category_id = c.id
where s.id = #{id}

select * from setmeal

    and name like concat('%',#{name},'%')
    and category_id = #{categoryId}
    and status = #{status}

select d.name, ds.copies, d.image, d.description
from dish_flavor ds left join dish d on ds.dish_id = d.id
where ds.setmeal_id = #{id}
